import React from 'react';
import { ContentTabs } from './content-tabs';

const FirstContent = () => <div>First content!</div>;
const SecondContent = () => <div>Second content!</div>;
const FirstTitle = () => <h4>First</h4>;
const SecondTitle = () => <h4>Second</h4>;

const content = [
  { title: 'First', body: <FirstContent /> },
  { title: 'Second', body: <SecondContent /> },
];

const contentB = [
  { title: <FirstTitle />, body: <FirstContent /> },
  { title: <SecondTitle />, body: <SecondContent /> },
];

export const BasicContentTabs = () => {
  return <ContentTabs tabsContent={content}></ContentTabs>;
};

export const ContentTabsWithUrlParam = () => {
  return (
    <ContentTabs tabsUrlParam="my_tabs" tabsContent={content}></ContentTabs>
  );
};

export const MultipleTabs = () => {
  return (
    <div>
      <ContentTabs tabsContent={content}></ContentTabs>
      <div style={{ height: 75 }} />
      <ContentTabs tabsContent={content}></ContentTabs>
    </div>
  );
};

export const ContentTabsWithJsxTitle = () => {
  return <ContentTabs tabsContent={contentB}></ContentTabs>;
};

const DynamicContent = () => {
  const [expanded, setExpanded] = React.useState(false);
  
  return (
    <div style={{ padding: '20px' }}>
      <h3>Dynamic Height Content</h3>
      <p>This section can expand and collapse to test dynamic resizing.</p>
      <button 
        onClick={() => setExpanded(!expanded)}
        style={{ marginBottom: '20px' }}
      >
        {expanded ? 'Show Less' : 'Show More'}
      </button>
      {expanded && (
        <div>
          <p>Extra content that appears when expanded!</p>
          {Array(5).fill(null).map((_, i) => (
            <p key={i}>Additional paragraph {i + 1} with some content to increase the height.</p>
          ))}
        </div>
      )}
    </div>
  );
};

const ShortContent = () => (
  <div style={{ padding: '20px' }}>
    Short content that takes minimal space!
  </div>
);

const LongContent = () => (
  <div style={{ padding: '20px' }}>
    <h3>Long Content Section</h3>
    <p>This is a much longer content section to test resize functionality.</p>
    {Array(10).fill(null).map((_, i) => (
      <div key={i} style={{ marginBottom: '20px' }}>
        <h4>Section {i + 1}</h4>
        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.</p>
        <ul>
          <li>Important point {i + 1}</li>
          <li>Another crucial detail</li>
          <li>More information here</li>
        </ul>
      </div>
    ))}
  </div>
);

const mixedContent = [
  { title: 'Short', body: <ShortContent /> },
  { title: 'Long', body: <LongContent /> },
  { title: 'Dynamic', body: <DynamicContent /> }
];


export const DynamicLengthContentTabs = () => {
  return <ContentTabs tabsContent={mixedContent} />;
};
