import React from 'react';
import classNames from 'classnames';
import { Icon } from '@teambit/evangelist.elements.icon';
import styles from './anchor.module.scss';

export type AnchorProps = {
  /**
   * specifies the anchor link to the section
   */
  href: string;
} & React.AnchorHTMLAttributes<HTMLAnchorElement>;

/**
 * An anchor component with an icon for linking your docs
 */
export function Anchor({ href, className, ...restProps }: AnchorProps) {
  const hashedHref = href && `#${href}`;

  return (
    <a
      id={href}
      href={hashedHref}
      className={classNames(className, styles.anchor)}
      {...restProps}
    >
      <Icon of="anchor" className={styles.anchorIcon} />
    </a>
  );
}
