type HtmlElementBasic = {
    parent: 'head' | 'body' | string;
    position: 'prepend' | 'append';
};
type HtmlElementWithAttributes = HtmlElementBasic & {
    attributes?: {
        [key: string]: string;
    };
    tag: string;
    content?: string;
    raw?: never;
};
type HtmlElementWithRaw = HtmlElementBasic & {
    raw?: string;
    attributes?: never;
    tag?: never;
    content?: never;
};
export type InjectedHtmlElement = HtmlElementWithAttributes | HtmlElementWithRaw;
/**
 * Simple function to inject an element into an html string.
 * @param html
 * @param element
 * @returns html with injected element
 */
export declare function inject(html: string, element: InjectedHtmlElement): string;
export {};
