import React from 'react';
import { inject } from './inject-html-element';

const basicHtml = `<html>
  <head>
    <title>My page</title>
  </head>
  <body>
    <h1>My page</h1>
    <p>This is my page.</p>
  </body>
</html>`;

export function InjectScriptIntoHead() {
  return (
    <pre>
      <code>
        {inject(basicHtml, {
          position: 'append',
          parent: 'head',
          tag: 'script',
          attributes: {
            src: 'https://example.com/script.js',
          },
        })}
      </code>
    </pre>
  );
}
